import 'controller/home_container_controller.dart';import 'package:flutter/material.dart';import 'package:tickpick/core/app_export.dart';import 'package:tickpick/presentation/all_activity_page/all_activity_page.dart';import 'package:tickpick/presentation/home_page/home_page.dart';import 'package:tickpick/presentation/profile_two_page/profile_two_page.dart';import 'package:tickpick/presentation/trending_sounds_tab_container_page/trending_sounds_tab_container_page.dart';import 'package:tickpick/widgets/custom_bottom_bar.dart';class HomeContainerScreen extends GetWidget<HomeContainerController> {@override Widget build(BuildContext context) { return SafeArea(child: Scaffold(backgroundColor: ColorConstant.whiteA700, body: Navigator(key: Get.nestedKey(1), initialRoute: AppRoutes.homePage, onGenerateRoute: (routeSetting) => GetPageRoute(page: () => getCurrentPage(routeSetting.name!), transition: Transition.noTransition)), bottomNavigationBar: CustomBottomBar(onChanged: (BottomBarEnum type) {Get.toNamed(getCurrentRoute(type), id: 1);}))); } 
///Handling route based on bottom click actions
String getCurrentRoute(BottomBarEnum type) { switch (type) {case BottomBarEnum.Home: return AppRoutes.homePage; case BottomBarEnum.Discover: return AppRoutes.trendingSoundsTabContainerPage; case BottomBarEnum.Inbox: return AppRoutes.allActivityPage; case BottomBarEnum.Profile: return AppRoutes.profileTwoPage; default: return "/";} } 
///Handling page based on route
Widget getCurrentPage(String currentRoute) { switch (currentRoute) {case AppRoutes.homePage: return HomePage(); case AppRoutes.trendingSoundsTabContainerPage: return TrendingSoundsTabContainerPage(); case AppRoutes.allActivityPage: return AllActivityPage(); case AppRoutes.profileTwoPage: return ProfileTwoPage(); default: return DefaultWidget();} } 
 }
